import 'package:wtfc_provider_app/export.dart';
import 'dart:math';

Future<void> saveRecipeItemsToFirebase() async {
  final CollectionReference ref =
      FirebaseFirestore.instance.collection("recipeItemsCollection");
  for (final Recipe recipe in listOfRecipes) {
    final String id =
        DateTime.now().toIso8601String() + Random().nextInt(1000).toString();
    ref.doc("das");
    await ref.doc(id).set(recipe.toMap());
  }
}

class Recipe {
  final String category;
  final String image;
  final String name;
  final int time;
  final int cal;
  final String rate;
  final double reviews;
  final String url;
  final List<String> ingredientsAmount;
  final List<String> ingredientsImage;
  final List<String> ingredientsName;
  final List<String> steps;

  Recipe(
      {required this.category,
      required this.image,
      required this.name,
      required this.time,
      required this.url,
      required this.cal,
      required this.rate,
      required this.reviews,
      required this.ingredientsAmount,
      required this.ingredientsImage,
      required this.ingredientsName,
      required this.steps});

  Map<String, dynamic> toMap() {
    return <String, dynamic>{
      'category': category,
      'image': image,
      'name': name,
      'time': time,
      'cal': cal,
      'rate': rate,
      'reviews': reviews,
      'ingredientsAmount': ingredientsAmount,
      'ingredientsImage': ingredientsImage,
      'ingredientsName': ingredientsName,
      'steps': steps,
    };
  }
}

